import './index.css';

const demo = (props) => {
    let x = props.x;
    let y = props.y;
    let z = props.z;
  
    if((x===y) && (y===z)){
      return(
        <>
          <div className="Slot_inner">
            <h1>
              {x} {y} {z}
            </h1>
            <h1>This is matching</h1>
            <hr/>
          </div>
        </>
      );
    }
    else{
      return(
        <>
          <div className="Slot_inner">
            <h1>
              {x} {y} {z}
            </h1>
            <h1>This is Not matching</h1>
            <hr/>
          </div>
        </>
      );
    }
  };

  const Slot1 = () => {
    return (
        <>
            <h1 className='Heading_style'>
        🎰Welcome to <span style={{fontWeight:"bold"}}>Slot Machine Game</span>🎰
      </h1>
      <div className='slotmachine'>
        <SlotM x = '😂' y = '😂' z = '😂'/>
        <SlotM x = '❤️' y = '😂' z = '😂'/>
        <SlotM x = '😂' y = '🌞' z = '😂'/>
      </div>
        </>
    )
  };
